INSERT INTO `groups` (`id`, `category_id`, `url`, `title`, `description`, `moderator`, `img`) VALUES
(1236, 5, 'https://vk.com/prazdnik888belova', 'Ведущая/Ирина Белова /Самара и область', '- Ищешь веселого и зажигательного ведущего???<br>\r\n- Хочешь, чтобы твой праздник прошел незабываемо круто, чтобы о нем вспоминали всю жизнь???<br>\r\n- Переживаешь что будет дорогая цена???<br>\r\nНе переживай!<br>\r\nВедь весело, не значит дорого!!!!!<br>', 1, '5-1236'),
(1237, 5, 'https://vk.com/astafurov_event', 'Ведущий |Антон Астафуров| Самара', 'Свадьбы,<br>\r\nЮбилеи/Дни Рождения,<br>\r\nКорпоративные мероприятия и тимбилдинги,<br>\r\nВыпускные,<br>\r\nПрезентации/Показы<br>', 1, '5-1237'),
(1238, 5, 'https://vk.com/club171600678', 'Ведущая | Самара | Эльмира', 'Друзья, что такое праздник?\r\n<br>\r\nЕсть чудное выражение - умение из ничего сделать конфетку. Это искусство приготовить пир из нехитрой снеди, созвать пару друзей и устроить грандиозный праздник. На это способен не только богач, но любой, у кого есть талант быть счастливым.\r\n<br>\r\nЭлизабет Гилберт<br>', 1, '5-1238'),
(1239, 5, 'https://vk.com/happyholiday63', 'Ведущий | Михаил Рожнов | Самара-Санкт-Петербург', 'Здравствуй, друг! Ты попал на страницу ведущего в Самаре. В частности: свадьбы, дни рождения, юбилеи, мероприятия городского масштаба.', 1, '5-1239'),
(1240, 5, 'https://vk.com/avmshow', 'Ведущий Антон Михайлов', 'Организатор мероприятий<br>\r\nРежиссер СГПУ<br>\r\nВедущий<br>', 1, '5-1240'),
(1241, 5, 'https://vk.com/babaevaelena91', 'Ведущая Вашего праздника, тамада САМАРА СУХОДОЛ', 'Lucky day любой праздник для Вас)<br>\r\nИменно здесь вы можете найти веселого креативного ведущего!<br>', 1, '5-1241'),
(1242, 5, 'https://vk.com/63tamada', 'Ведущий, тамада на праздник в Самаре', 'Проведение свадеб, юбилеев, корпоративов и т.д. и то сё в Самаре и области', 1, '5-1242'),
(1243, 5, 'https://vk.com/antonsamohvalov', 'Ведущий мероприятий Антон Самохвалов', 'Теле- и радио-ведущий Антон Самохвалов. Организация и проведение праздников. Написание оригинальных сценариев. Дискотека. Конкурсы, сюрпризы и розыгрыши на любой вкус. Индивидуальный подход к каждому клиенту!', 1, '5-1243'),
(1244, 5, 'https://vk.com/duetksv', 'Ведущие Самара.Дуэт \"K&V\". Свадьбы.Юбилеи.', 'В арсенале у нас:<br>\r\n- Ведущие<br>\r\n- Ди-джей<br>\r\n- Проф. аппаратура<br>\r\n- Вокал (живой звук), хореография<br>\r\n- Декор<br>\r\n- Фотосъемка<br>\r\n- Шоу-программы<br>\r\n- Свадьбы, юбилеи, корпоративы<br>\r\n- Детские праздники, выпускные<br>', 1, '5-1244'),
(1245, 5, 'https://vk.com/rayhantamada', 'ВЕДУЩАЯ РАИСА, ДИДЖЕЙ ВИКТОРИЯ', 'Мы с удовольствием проведем вашу свадьбу, юбилей, корпоратив, выпускной. Работаем качественно и зажигательно!!!!!!!', 1, '5-1245'),
(1246, 5, 'https://vk.com/grivol0110', 'Ксения Волкова | Ведущий на свадьбу | Самара', 'Ведущая (поющая)<br>\r\nDJ + все необходимое оборудование<br>\r\nАвторская программа<br>\r\nАртисты<br>\r\nВыездная регистрация<br>', 1, '5-1246'),
(1247, 5, 'https://vk.com/httpsvk.comlnikolaenkova', '#Арт_дуэт _Желаем _счастья_ Ведущая и DJ Самарa', 'Проведение и музыкальное сопровождение свадеб юбилеев корпоративов<br>\r\nСовременно Стильно Весело<br>', 1, '5-1247'),
(1248, 5, 'https://vk.com/public192445614', 'Ведущий Арифулин Руслан', 'Меня зовут Арифулин Руслан и я ведущий на ваше мероприятие. Ведущий в EVENT-индустрии с 2018 года. Во время планирования мероприятия вы столкнетесь с массой проблем начиная от выбора ресторана до выбора ведущего. И все это отнимет у вас кучу времени. И в итоге вы выберите ведущего в последний момент, DJ которого, будет включать Вам Metallica или Сердючку. А еще лучше если и то и другое. Оно вам надо? Не думайте о команде Event. Вам предстоят свадьбы, корпоративное мероприятие, юбилей или благотворительный гала-вечер? Перейдите в раздел информация группы и вы увидите мои контакты! Я делаю все для того, чтобы каждое организованное мной торжество было вдохновляющим, уникальным и незабываемым.', 1, '5-1248'),
(1249, 2, 'https://vk.com/fotograf_pafos', 'Свадебный Фотограф Самара', 'Мы команда профессионалов, которая более 11 лет занимается свадебной фотосъемкой. Работаем на профессиональных фотоаппаратах Canon и Nikon с большим набором сменной оптики, света и доп. оборудования.\r\n<br>\r\nСпец. предложение месяца: СВАДЕБНЫЙ ФОТОГРАФ за 9.900 руб.<br>', 1, '2-1249'),
(1250, 2, 'https://vk.com/club66031518', 'Свадебный фотограф Самара Дарья Солнцева', 'В своих работах я делаю акцент на искренних эмоциях, чувствах, нежности и романтизме. открыта бронь свадебных дат 2020!\r\n<br>\r\n', 1, '2-1250'),
(1251, 2, 'https://vk.com/crazyladystudio63', 'СВАДЕБНЫЙ И СЕМЕЙНЫЙ ФОТОГРАФ САМАРА', '', 1, '2-1251'),
(1252, 2, 'https://vk.com/korolevaphotok', '@ K O R O L E V A @ Свадебный Фотограф в Самаре', 'Сохраните нежность на фотографиях<br>\r\nПомощь в планировании свадебного дня<br>\r\nПомощь в выборе фотостудии, мест для проведения фотосессии<br>\r\nСвадебный видеоролик<br>\r\nБолее 50 фотографий в обработке и высоком качестве для печати<br>\r\nВсе фотографии на онлайн диске (200-700)<br>\r\nВ течение недели первые фотографии', 1, '2-1252'),
(1253, 2, 'https://vk.com/alyansphoto', 'Свадебный фотограф в Самаре', 'Свадебный фотограф в Самаре | Фотосъемка в Самаре | Фотограф Самара | Фотограф на свадьбу в Самаре<br>\r\nПреимущества работы с нами:<br>\r\nПрофессиональное Фото-Световое оборудование, Выездная студия<br>\r\nБольшой опыт работы в сфере свадебной фотосъемки<br>\r\nДоступная и стабильная стоимость, Максимальное качество в короткие сроки<br>', 1, '2-1253'),
(1254, 2, 'https://vk.com/samara_fotograf', 'Фотограф Алексей Чехов', 'Добрый день, я свадебный фотограф.<br>\r\nЯ безумно рад видеть Вас на моей страничке в ВКонтакте.<br>\r\nЯ свадебный фотограф со стажем работы более 7 лет. Работаю исключительно на профессиональном оборудовании Canon 5D Mark IV с огромным арсеналом сменной оптики L-серии и студийным светом.<br>\r\nВозможно ли на свадьбе рассказать эмоцию? А объяснить лучезарную улыбку или сияние глаз невесты? Свадебное фото способно остановить это радостное мгновение и вписать его в вечность.<br>', 1, '2-1254'),
(1255, 2, 'https://vk.com/olesiasidoruk', 'Фотограф в Самаре Олеся Сидорук', 'Всем привет! Меня зовут Олеся и я фотограф\r\nдля девушек,беременных, семей и деток !<br>\r\nНо я не только фотограф но и мама, именно поэтому точно знаю что такое счастье материнства, что сердечко бьется внутри, сияющие глаза малыша и насколько быстро растут детки.<br>\r\nА еще знаю , как важно сохранять самые счастливые моменты в памяти и на фотоснимках, именно поэтому вкладываю в каждую фотосъемку часть своей души, чтобы Ваши снимки и через десятки лет вызвали в Вас самые светлые эмоции !<br>', 1, '2-1255'),
(1256, 2, 'https://vk.com/chekmaxim', 'СВАДЕБНЫЙ И СЕМЕЙНЫЙ ФОТОГРАФ В САМАРЕ', '', 1, '2-1256'),
(1257, 2, 'https://vk.com/elena_antipova_photo', 'Фотограф на свадьбу в Самаре', 'Профессиональный фотограф на свадьбу в Самаре - Елена Антипова', 1, '2-1257'),
(1258, 2, 'https://vk.com/gribtcova_foto', 'Свадебный фотограф Самара', 'Здравствуйте!<br>\r\nМеня зовут Людмила Грибцова. Я с удовольствием работаю на свадьбах и запечатлею самые интересные и яркие моменты вашего дня!\r\n<br>\r\nДелаю съемку не только полного дня, но и нескольких часов. Буду рада ответить на интересующие вас вопросы и помочь вам сделать день свадьбы незабываемым!<br>', 1, '2-1258'),
(1259, 2, 'https://vk.com/fotobukinalarisa', 'Свадебный фотограф в Самаре.', 'Здравствуйте, меня зовут Лариса!) Люблю снимать счастливых людей!) Верю в любовь. Помогаю людям, которые не любят и не умеют фотографироваться, комфортно чувствовать себя в кадре. Свадебная фотосъемка для меня не только работа, но и источник вдохновения и энергии. По этому съемочный день проходит на позитиве!)', 1, '2-1259'),
(1260, 2, 'https://vk.com/kleemof.foto', 'Фотограф | KLEEMOF | Самара', '', 1, '2-1260'),
(1261, 2, 'https://vk.com/ivovichfotosamara', 'Семейный Фотограф-Видеограф *Елена Медведева*', 'Добро пожаловать в мою группу! Мне нравится проводить семейные, детские,\r\nиндивидуальные съемки, такие как: Love-Story, В ожидании малыша, Детские дни рождения, Родители и малыш, Крестины, Девичники, и все прочее что происходит с вами в вашей жизни)<br>\r\nТак же я создаю видеослайдшоу из ваших фото, по вашему желанию создам для вас фото книгу и распечатаю понравившиеся вам фотографии)<br>', 1, '2-1261'),
(1262, 2, 'https://vk.com/weddinginsamara', 'Оцифровка эмоций! Фотограф Толя Саркан', 'Сохраняю эмоции и чувства) С любовью!) Студия живой фотографии \"Оцифровка эмоций!\"', 1, '2-1262'),
(1263, 2, 'https://vk.com/clubmaria_vedo', 'Maria Vedo Photographer. Фотограф в Самаре.', 'Профессиональный фотограф Maria Vedo\r\n<br>\r\nДуша в каждом кадре - Soul in each shot<br>', 1, '2-1263'),
(1264, 2, 'https://vk.com/lenavanphoto', 'Photographer Lena Van', '-Свадебная<br>\r\n-семейная<br>\r\n-портретная<br>\r\n-репортажная<br>\r\nсъемка<br>', 1, '2-1264'),
(1265, 2, 'https://vk.com/babyfelince', 'Детский, семейный фотограф Самара | Алина Пич', 'Приветствую всех в своей группе! Меня зовут Алина, я детский и семейный фотограф. Хочу поделиться с своими работами и пригласить вас на фотосессии ♥ Я имею опыт в фотосъемке более 7 лет и умею создавать приятную атмосферу на фотосессиях, и даже самые скромные детишки и взрослые начинают позировать на камеру ♡ Если вы хотите пополнить свой семейный альбом яркими, профессиональными фотографиями, на которых будут запечатлены самые милые и тёплые эмоции, то вам точно ко мне ;) Работаю во всех фотостудиях Самары, помогу вам с выбором и бронированием подходящей студии.', 1, '2-1265'),
(1266, 2, 'https://vk.com/fotograf_samara_svadba', 'Фотограф Самара', 'Добрый день.<br>\r\nЯ фотограф Самары, но фотографирую по всему Миру. Специализируюсь, как свадебный фотограф, но так же иногда снимаю видео. У меня есть школа фотографии, так же провожу МК для фотографов. Образование высшее, художественное. Умею раскрывать лучшие стороны людей, показывать настоящие эмоции, особенно на свадьбах. Люблю находить красивый ракурс, не обычно падающий свет и просто счастлив когда все это сливается в одном свадебном кадре. Остальное. обо мне скажут мои свадебные фотографии. Приятного просмотра. Буду рад стать вашим свадебным фотографом в Самаре и не только.)<br>\r\nВаш свадебный фотограф Самары Алексей Чехов.<br>', 1, '2-1266'),
(1267, 2, 'https://vk.com/newborn_photo_samara', 'Фотограф новорожденных Самара', 'Фотосессии беременности, новорожденных, деток до 1 года', 1, '2-1267'),
(1268, 2, 'https://vk.com/mastroyakes', 'Фотограф', '', 1, '2-1268'),
(1269, 2, 'https://vk.com/mariashumikhinaphoto', 'MARIA SHUMIKHINA PHOTO ФОТОГРАФ САМАРА', 'Портретная, LoveStory, детская, предсвадебная съемка.', 1, '2-1269'),
(1270, 2, 'https://vk.com/victoriavins', 'Фотограф Виктория Винс', 'У меня сниматься интересно даже мужчинам!<br>\r\nВо время съёмок они не говорят: «Я устал!», «Хватит уже».\r\n<br>\r\nСоздаю живые и яркие снимки! Вы живёте в кадре, наслаждаясь друг другом, а я Вас снимаю. Вместе с Вами у нас получается красивое фотокино. Умею работать с парами разного возраста.\r\n<br>\r\nМужчинам у меня сниматься легко, так как им не приходится позировать, поэтому они не устают, а тоже получают удовольствие от процесса.\r\n<br>\r\nФотокнига в подарок при раннем бронировании.\r\n<br>\r\nСлайдшоу прямо на Вашей свадьбе! Похвастаться вашим счастливым днём перед подругами, друзьями, родственниками - это дорогого стоит!\r\n<br>\r\nМои фотографии имеют яркие и чистые цвета без фильтров и тонировок.<br>', 1, '2-1270'),
(1271, 2, 'https://vk.com/svadebnfoto', 'Фотограф,Видеограф в Самаре', 'Наша фото видеостудия - ваш шанс сделать качественные фото и видео съёмки. Направления: свадьбы, предметка, интерьер, студия, детские, выпуски, юбилеи, днюхи на дачах, репортаж. Большой опыт и реальное наличие техники проф. свет, штативы, оптика. Никакого обмана. Сжатые сроки. Рекомендации и положительные отзывы.', 1, '2-1271'),
(1272, 2, 'https://vk.com/tikhonova_photo', 'Фотограф в Самаре | фотосессия Кристина Тихонова', '', 1, '2-1272'),
(1273, 2, 'https://vk.com/bronzovclub', 'Свадебный фотограф Антон Бронзов', 'Немного о себе:<br>\r\nМне 31 год, женат, живу в Самаре<br>\r\nФотографией занимаюсь с 2008 года<br>\r\nОсновная деятельность - свадебные фото, но в перерывах также занимаюсь фотосессиями и репортажной съемкой<br>\r\nНе скуплюсь на результат. Если фото красивые и ценные - все равно их обрабатываю, не привязываясь четко к фиксированному количеству<br>', 1, '2-1273'),
(1274, 2, 'https://vk.com/malikovaphotosamara', 'Фотограф в Самаре Маликова Екатерина', 'Провожу фотосессии в студии(в любом другом помещении) и на улице:<br>\r\n1) Репортажные (Детские и взрослые праздники, корпоративы и т.д.)<br>\r\n2) Индивидуальные<br>\r\n3) Love story<br>\r\n4) Свадебные<br>\r\n5) Семейные<br>\r\n6) Будущих мам<br>\r\n7) Детские<br>', 1, '2-1274'),
(1275, 2, 'https://vk.com/olesyasigachevafoto', 'Репортажный, свадебный фотограф в Самаре и по России.', 'Я всегда подскажу, что нужно сделать, чтобы фотографии получились красивыми,а Ваши улыбки искренними без дополнительных усилий.\r\n<br>\r\nИ ваш семейный альбом обязательно будет хранить трогательные и ценные мгновения счастья, улыбок и моментов, которые больше никогда не повторяться.\r\n<br>\r\nЗимними вечерами вы будите им улыбаться,нежно обнимая друг друга так как это будут не только дежурные,классические позы!\r\n<br>\r\nЯ верю,что изменится ваше отношение к фотографии, увидев драйф, красоту и искренность момента<br>', 1, '2-1275'),
(1276, 2, 'https://vk.com/tolmacheva_photosamara', 'Фотограф в Самаре. Толмачева Ирина', 'индивидуальные фотосессии, семейные, детские.<br>\r\nЗдравствуйте! Меня зовут Ирина и я рада приветствовать Вас в своей группе!<br>\r\nСама очень простая, веселая, открытая и ценю это в других людях. Иногда сама появляюсь в кадре в качестве модели.<br>\r\nЖду Вас на фотосъемки: студийные, фотосессии, фотопрогулки и т.д.! С Вами мы сможем придумать что-то новое и интересное!\r\nЕсли возникнут сложности с позированием, не волнуйтесь! С удовольствием Вам помогу!\r\nРада буду Вас видеть!', 1, '2-1276'),
